import { createContext, useEffect, useState, useContext} from "react";
import { useReducer } from "./reducer.service";


export const ContextGlobal = createContext();


export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  const [state, dispatch] = useReducer(reducer, initialState);

  const providerState = use(
    () => ({
      data: state.data,
      theme: state.theme,
      setData: (item) => {
        dispatch({ type: actions.setData, payload: item });
      },
      setDarkTheme: () => {
        dispatch({ type: actions.setThemeDark });
      },
      setLightTheme: () => {
        dispatch({ type: actions.themeLight });
      },
    }),
    [state, dispatch]
  );

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((data) => providerState.setData(data));
  }, [providerState]);

  return (
    <ContextGlobal.Provider value={providerState}>
      {children}
    </ContextGlobal.Provider>
  );
};
