import React, { useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const [name, setName] = useState('')
  const [mail, seteMail] = useState('')
  const [success, setSuccess] = useState('')
  const [fail, setFail] = useState('')

  const handleSubmit = (e) =>{
    e.preventDefault();
    if(name.length > 3){
      setSuccess(true)
    }else{
      setFail(true)
    }
  }
  return (
    <div>
      <form>
        <h3>Envianos tus datos y te contactamos </h3>
        <label htmlFor="name">Nombre</label>
        <input type="text" value={name} disabled={success} onChange={(e)=> setName(e.target.value)}/>
        <label htmlFor="email">Email</label>
        <input type="text" value={mail} disabled={success} onChange={(e)=> setMail(e.target.value)}/>
        <button>Enviar Datos</button>
        {fail && 'Chequea la informacion ingresada'}
      </form>
    </div>
  );
};

export default Form;
